package com.webweeb.backend.config;import com.fasterxml.jackson.core.JsonGenerator;import com.fasterxml.jackson.databind.JsonSerializer;import com.fasterxml.jackson.databind.SerializerProvider;import java.io.IOException;public class EnumSerializer<T extends Enum<T>> extends JsonSerializer<T> {    /**     * Serializes an enum value to its name.     *     * @param value the enum value to serialize     * @param gen the JSON generator     * @param serializers the serializer provider     * @throws IOException if an I/O error occurs     */    @Override    public void serialize(T value, JsonGenerator gen, SerializerProvider serializers) throws IOException {        gen.writeString(value.name());    }}