package com.webweeb.backend.config;import com.fasterxml.jackson.databind.ObjectMapper;import com.fasterxml.jackson.databind.SerializationFeature;import com.fasterxml.jackson.databind.module.SimpleModule;import com.fasterxml.jackson.datatype.jsr310.JavaTimeModule;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class JacksonConfig {    /**     * Configures the ObjectMapper to handle Java 8 date/time types and custom serialization.     *     * @return the configured ObjectMapper     */    @Bean    public ObjectMapper objectMapper() {        ObjectMapper objectMapper = new ObjectMapper();        objectMapper.registerModule(new JavaTimeModule());        objectMapper.disable(SerializationFeature.WRITE_DATES_AS_TIMESTAMPS);        SimpleModule module = new SimpleModule();        module.addSerializer(Enum.class, new EnumSerializer());        objectMapper.registerModule(module);        return objectMapper;    }}