package com.webweeb.backend.config;import org.modelmapper.Conditions;import org.modelmapper.ModelMapper;import org.modelmapper.convention.MatchingStrategies;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class ModelMapperConfig {    /**     * Configures a ModelMapper bean with strict matching strategy and non-null property condition.     *     * @return a configured ModelMapper instance     */    @Bean    public ModelMapper modelMapper() {        ModelMapper modelMapper = new ModelMapper();        modelMapper.getConfiguration()                .setMatchingStrategy(MatchingStrategies.STRICT)                .setPropertyCondition(Conditions.isNotNull());        return modelMapper;    }}