package com.webweeb.backend.controller;import com.webweeb.backend.dto.LoginHistoryDTO;import com.webweeb.backend.response.ApiResponse;import com.webweeb.backend.service.LoginHistoryService;import io.swagger.v3.oas.annotations.tags.Tag;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@Slf4j@RestController@RequestMapping("/api/login-history")@Tag(name = "Login History", description = "Endpoints for managing user login history")public class LoginHistoryController {    @Autowired    private LoginHistoryService loginHistoryService;    /**     * Retrieve login history for the authenticated user.     *     * @return ApiResponse containing a list of login history records.     */    @GetMapping    public ResponseEntity<ApiResponse<List<LoginHistoryDTO>>> getCurrentUserLoginHistory() {        List<LoginHistoryDTO> loginHistory = loginHistoryService.getCurrentUserLoginHistory();        return ResponseEntity.ok(ApiResponse.success(loginHistory, "Login history retrieved successfully"));    }    @GetMapping("/login-patterns")    public ResponseEntity<List<LoginHistoryDTO>> getSuspiciousLogins() {        List<LoginHistoryDTO> suspiciousLogins = loginHistoryService.analyzeLoginPatterns();        return ResponseEntity.ok(suspiciousLogins);    }}