package com.webweeb.backend.dto;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.time.LocalDateTime;import java.util.UUID;/** * Base Data Transfer Object (DTO) class that contains common fields for all DTOs. * This class is intended to be extended by other DTO classes. */@Getter@Setter@AllArgsConstructor@NoArgsConstructorpublic class BaseDTO {    @JsonProperty(access = JsonProperty.Access.READ_ONLY)    private UUID id;    private LocalDateTime createdAt;    private LocalDateTime updatedAt;}