package com.webweeb.backend.entity;import jakarta.persistence.*;import lombok.Data;import org.springframework.data.jpa.domain.support.AuditingEntityListener;import java.time.LocalDateTime;import java.util.UUID;@MappedSuperclass@Data@EntityListeners(AuditingEntityListener.class)public abstract class BaseEntity {    @Id    @GeneratedValue(strategy = GenerationType.UUID)    @Column(updatable = false, nullable = false)    private UUID id;    @Column(name = "created_at", updatable = false, nullable = false)    private LocalDateTime createdAt;    @Column(name = "updated_at")    private LocalDateTime updatedAt;    @PrePersist    protected void onCreate() {        this.createdAt = LocalDateTime.now();    }    @PreUpdate    protected void onUpdate() {        this.updatedAt = LocalDateTime.now();    }}