package com.webweeb.backend.entity;import com.webweeb.backend.enums.RoleType;import jakarta.persistence.*;import lombok.*;import org.springframework.security.core.GrantedAuthority;@Entity@EqualsAndHashCode(onlyExplicitlyIncluded = true, callSuper = false)@Data@AllArgsConstructor@NoArgsConstructor@Builder@Table(name = "\"role\"")public class Role extends BaseEntity implements GrantedAuthority {    @EqualsAndHashCode.Include    @Column(unique = true, nullable = false)    @Enumerated(EnumType.STRING)    private RoleType name;    @Override    public String getAuthority() {        return "ROLE_" + name;    }}