package com.webweeb.backend.repository;import com.webweeb.backend.entity.LoginHistory;import com.webweeb.backend.entity.User;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;import java.util.List;import java.util.UUID;@Repositorypublic interface LoginHistoryRepo extends JpaRepository<LoginHistory, UUID> {    List<LoginHistory> findByUser(User user);    @Query("SELECT l.ipAddress FROM LoginHistory l WHERE l.user.id = :id")    List<String> findAllIpAddressesByUserId(@Param("id") UUID id);}