package com.webweeb.backend.repository;import com.webweeb.backend.entity.User;import jakarta.validation.constraints.NotBlank;import jakarta.validation.constraints.Size;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import java.util.Optional;import java.util.UUID;@Repositorypublic interface UserRepo extends JpaRepository<User, UUID> {    Optional<User> findByUsernameOrEmail(String username, String email);    boolean existsByUsernameOrEmail(@NotBlank(message = "Username is required") @Size(min = 3, max = 30, message = "Username must be between 3 and 30 characters") String username,                                    @NotBlank(message = "Email is required") String email);}