package com.webweeb.backend.response;import lombok.*;import org.springframework.http.HttpStatus;import java.time.Instant;import java.util.Map;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Builderpublic class ApiResponse<T> {    private boolean success;    private String message;    private HttpStatus status;    private Instant timestamp;    private T data;    /**     * Static factory methods for creating ApiResponse instances.     */    public static <T> ApiResponse<T> success(T message) {        return ApiResponse.<T>builder()                .success(true)                .message("Success")                .status(HttpStatus.OK)                .timestamp(Instant.now())                .data(message)                .build();    }    /**     * Static factory method for creating ApiResponse instances with a custom message.     */    public static <T> ApiResponse<T> success(T data, String message) {        return ApiResponse.<T>builder()                .success(true)                .message(message)                .status(HttpStatus.OK)                .timestamp(Instant.now())                .data(data)                .build();    }    public static <T> ApiResponse<T> error(String message, HttpStatus status) {        return ApiResponse.<T>builder()                .success(false)                .message(message)                .status(status)                .timestamp(Instant.now())                .data(null)                .build();    }    public static <T> ApiResponse<T> error(Map<String, String> message, HttpStatus status) {        return ApiResponse.<T>builder()                .success(false)                .message(message.toString())                .status(status)                .timestamp(Instant.now())                .data(null)                .build();    }}